import React, { useState } from 'react'

function FeedbackStats({feedback}) {
    // console.log(feedback);
    const [numOfFeedback, setNumOfFeedback] = useState(
        (feedback.length) ? feedback.length 
        : 0
    )

    const [rating, setRating] = useState(
        (feedback.length) ? feedback.map(item => item.rating).reduce((c,v) => c+v)/numOfFeedback
        : "No ratings yet")

    return (
    <div>
        <p>reviews: {numOfFeedback}</p>
        <p>Average ratings: {rating}</p>
    </div>
    )
}

export default FeedbackStats
