import React, { useState } from 'react'

function FeedbackStats({feedback}) {
    // console.log(feedback);
    const feedbackLength = (feedback.length) ? feedback.length : 0
    const [numOfFeedback, setNumOfFeedback] = useState(feedbackLength)

    const [rating, setRating] = useState(
        (feedback.length) ? (feedback.reduce((acc,curr) => acc + curr.rating, 0)/numOfFeedback).toFixed(2)
        : "No ratings yet")

    return (
    <div className='feedback-stats'>
        <h4>Reviews: {numOfFeedback}</h4>
        <h4>Average ratings: {rating}</h4>
    </div>
    )
}

export default FeedbackStats
